mapper.update:
UPDATE product

	prod_name = #{prodName},
	prod_detail = #{prodDetail},
	manufacture_day = #{manuDate:VARCHAR},
	price = #{price}

WHERE prod_no = #{prodNo}

mapper.select:
SELECT *
FROM ( SELECT inner_table.*, ROWNUM AS row_seq
		FROM ( SELECT p.prod_no, p.prod_name, p.prod_detail, p.price, NVL(t.tran_status_code, 0)
				FROM product p, transaction t
				
					p.prod_no = t.prod_no(+)
					
						
							AND p.prod_name LIKE #{searchKeyword}
						
						
							AND p.prod_detail LIKE #{searchKeyword}
						
					
					
					
						
							AND p.price BETWEEN #{searchMin} AND #{searchMax}
						
						
							AND p.price <= #{searchMax}
						
					
					
						
							AND p.price >= #{searchMin}
						
					
					
					
						
							AND t.tran_status_code IS NULL
						
						
							AND t.tran_status_code IN ( #{searchCode} )
						
					
				
				ORDER BY
					
						p.reg_date DESC
					
					
						p.price DESC
					
					
						p.price
					
				) inner_table
		WHERE ROWNUM <= #{endRowNum} )
WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*)
FROM ( SELECT p.prod_no, p.prod_name, p.prod_detail, p.price, NVL(t.tran_status_code, 0)
		FROM product p, transaction t
		
			p.prod_no = t.prod_no(+)
			
				
					AND p.prod_name LIKE #{searchKeyword}
				
				
					AND p.prod_detail LIKE #{searchKeyword}
				
			
			
			
				
					AND p.price BETWEEN #{searchMin} AND #{searchMax}
				
				
					AND p.price <= #{searchMax}
				
			
			
				
					AND p.price >= #{searchMin}
				
			
			
			
				
					AND t.tran_status_code IS NULL
				
				
					AND t.tran_status_code IN ( #{searchCode} )
				
			
		 ) countTable
