mapper.update:
UPDATE transaction
payment_option = #{paymentOption}, receiver_name = #{receiverName:VARCHAR}, receiver_phone = #{receiverPhone:VARCHAR}, demailaddr = #{divyAddr:VARCHAR}, dlvy_request = #{divyRequest:VARCHAR}, dlvy_date = #{divyDate:DATE}
WHERE tran_no = #{tranNo}

mapper.select:
SELECT *
FROM ( SELECT inner_table.*, ROWNUM AS row_seq
		FROM ( SELECT tran_no, prod_no, buyer_id, receiver_name, receiver_phone,
					  dlvy_date, tran_status_code, order_date, demailaddr, quantity
				FROM transaction
				
					
						buyer_id = #{buyerId}
					
					
						prod_no = #{search.searchKeyword}
					
					
					
						AND tran_status_code = ${search.searchCode}
					
				
				ORDER BY tran_no DESC ) inner_table
		WHERE ROWNUM <= #{search.endRowNum} )
WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT COUNT(*)
FROM ( SELECT tran_no, prod_no, buyer_id, receiver_name, receiver_phone,
			  dlvy_date, tran_status_code, order_date, demailaddr, quantity
		FROM transaction
		
			
				buyer_id = #{buyerId}
			
			
				prod_no = #{search.searchKeyword}
			
			
			
				AND tran_status_code = ${search.searchCode}
			
		
		ORDER BY tran_no DESC ) countTable
